import { authService } from "../myBase";
import React, { useState } from "react";
import "../css/Auth.css"




export default () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [newAccount, setNewAccount] = useState(true);
  const [error, setError] = useState("");



  const toggleAccount = () => setNewAccount((prev) => !prev);

  return (
    <div id="container" class="main_container">
        <div style={{padding: "70px"}}></div>
                <Login />
            </div>
  )
}



const Login = () => {
	const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

	const onSubmit = async (event) => {
    event.preventDefault();
    let data;
    try {
      let data;
      if (newAccount) {
        data = await authService.createUserWithEmailAndPassword(
          email, password
        );
      } else {
        data = await authService.signInWithEmailAndPassword(email, password);
      };
      console.log(data);
    } catch (error) {
      console.log(error.message);
      setError(error.message);
    }
  }

  const onChange = (event) => {
    const { target: { name, value } } = event;
    if (name === "username") {
      setId(value)
    } else if (name === "password") {
      setPassword(value);
    }
  }

	return(
	<>
		<form name="login_form" submit={onSubmit}>

                    <div className="form_title_div">
                        <p className="form_title_p">로그인</p>
                    </div>

                    <div>
                        <div>
                            <input type="text" name="username" value={id} placeholder="아이디" className="form_input" onChange={onChange}/>
                        </div>
                        <div className="form_text_alert_paddings">
                            <div id="alert_username" className="form_text_alert"></div>
                        </div>
                    </div>

                    <div>
                        <div>
                            <input type="password" name="password" value={password} placeholder="비밀번호" className="form_input" onChange={onChange}/>
                        </div>
                        <div className="form_text_alert_padding">
                            <div id="alert_password" className="form_text_alert"></div>
                        </div>
                    </div>

                    <div style={{height: "10px"}}></div>
                    <div>
                        <button type="button" className="form_submit_button" onClick={onSubmit}>로그인</button>
                    </div>

                    <div style={{height: "10px"}}></div>
                    <div>
                        <button type="button" className="form_register_button" onClick="register()">회원가입</button>
                    </div>

                    <div style={{height: "10px"}}></div>
                    <div>
                        <button type="button" className="form_findpasswd_button" onClick="find_passwd()">비밀번호 찾기</button>
                    </div>

                </form>
		</>)
}
